import React from "react";
import "./Chart.css";


import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts"

const data = [
  {
    date: "Apr 1, 2025",
    Sales: 110,
    Purchase: 28,
  },
  {
    date: "Apr 1, 2025",
    Sales: 79,
    Purchase: 39,
  },
  {
    date: "Apr 1, 2025",
    Sales: 54,
    Purchase: 24,
  },
]

const chartConfig = {
  Sales: {
    label: "Sales",
    color: "#c012e2",
  },
  Purchase: {
    label: "Purchase",
    color: "#0066ff",
  },
}

export function Chart() {
  return (
    <div className="flex flex-col items-center gap-6 p-6 bg-white rounded-lg shadow-sm" style={{ width: "450px" }}>
      <h2 className="text-2xl font-semibold text-foreground">Stocks Graph</h2>

      <div className="flex justify-center">
        <ResponsiveContainer width={400} height={550}>
          <BarChart data={data} margin={{ top: 20, right: 30, left: 0, bottom: 60 }}>
            <CartesianGrid strokeDasharray="0" stroke="#e5e7eb" vertical={false} />

            <YAxis
              type="number"
              domain={[0, 120]}
              ticks={[0, 30, 60, 90, 120]}
              label={{ value: "", angle: -90, position: "insideLeft" }}
              width={40}
              tick={{ fill: "#666" }}
            />

            <XAxis
              dataKey="date"
              tick={{ fill: "#666", fontSize: 12 }}
              angle={0}
              height={60}
              interval={0}
              tickMargin={15}
            />

            <Tooltip
              contentStyle={{
                backgroundColor: "#fff",
                border: "1px solid #ccc",
                borderRadius: "4px",
                padding: "8px",
              }}
              cursor={{ fill: "rgba(0, 0, 0, 0.05)" }}
            />

            <Bar dataKey="Sales" fill="#c012e2" radius={[4, 4, 0, 0]} isAnimationActive={false} />

            <Bar dataKey="Purchase" fill="#0066ff" radius={[4, 4, 0, 0]} isAnimationActive={false} />

            <Legend
              verticalAlign="bottom"
              height={36}
              wrapperStyle={{
                paddingTop: "20px",
              }}
              iconType="square"
            />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  )
}
