"use client"

import { useState } from "react"
import { FormInput } from "./FormInput"
import { FormButton } from "./FormButton"
import "./ForgotPasswordPage.css";

export default function ForgotPasswordPage({ onBackClick }) {
  const [email, setEmail] = useState("")
  const [errors, setErrors] = useState({})
  const [loading, setLoading] = useState(false)
  const [successMessage, setSuccessMessage] = useState("")

  const validateForm = () => {
    const newErrors = {}
    if (!email.trim()) {
      newErrors.email = "Email is required"
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = "Please enter a valid email"
    }
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleChange = (e) => {
    setEmail(e.target.value)
    if (errors.email) {
      setErrors({})
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!validateForm()) return

    setLoading(true)
    setSuccessMessage("")
    try {
      const response = await fetch("/forgot-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      })

      if (response.ok) {
        setSuccessMessage("Password reset link sent to your email!")
        setEmail("")
        setTimeout(() => onBackClick(), 3000)
      } else {
        setErrors({ submit: "Email not found. Please try again." })
      }
    } catch (error) {
      setErrors({ submit: "Network error. Please try again." })
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="forgot-container">
      <div className="forgot-background"></div>

      <div className="forgot-wrapper">
        <div className="forgot-card">
          <h1 className="forgot-title">FORGOT PASSWORD?</h1>
          <p className="forgot-subtitle">Enter your email to receive a password reset link.</p>

          {successMessage && (
            <div className="bg-green-100 text-green-800 p-3 rounded mb-4 text-sm">{successMessage}</div>
          )}
          {errors.submit && <div className="bg-red-100 text-red-800 p-3 rounded mb-4 text-sm">{errors.submit}</div>}

          <form onSubmit={handleSubmit} className="forgot-form-space">
            <FormInput
              label="Email Address"
              name="email"
              type="email"
              placeholder="ex. admin@gmail.com"
              value={email}
              onChange={handleChange}
              error={errors.email}
            />

            <FormButton type="submit" disabled={loading}>
              {loading ? "Sending..." : "Send Reset Link"}
            </FormButton>

            <div className="forgot-back-link">
              <button type="button" onClick={onBackClick}>
                Back to Login
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}
