import React from "react";
import { useNavigate } from "react-router-dom"; // <CHANGE> Add this import
import dashboardIcon from "../images/dashboardicon.png"
import inventoryIcon from "../images/inventoryicon.png"
import historyIcon from "../images/historyicon.png"
import sidebarGradient from "../images/sidebarbg.png"
import "./Sidebar.css";

export const Sidebar = () => {
  const [activeMenu, setActiveMenu] = React.useState("dashboard")
  const navigate = useNavigate(); // <CHANGE> Add this line

  const menuItems = [
    { id: "dashboard", label: "Dashboard", icon: dashboardIcon, path: "/" }, // <CHANGE> Add path
    { id: "inventory", label: "Inventory", icon: inventoryIcon, path: "/Inventory" }, // <CHANGE> Add path
    { id: "history", label: "History", icon: historyIcon, path: "/History" }, // <CHANGE> Add path
  ]

  return (
    <aside className="sidebar">
      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <button
            key={item.id}
            className={`sidebar-item ${activeMenu === item.id ? "active" : ""}`}
            onClick={() => { setActiveMenu(item.id); navigate(item.path); }} // <CHANGE> Add navigate call
          >
            <img src={item.icon || "/placeholder.svg"} alt={item.label} className="sidebar-icon" />
            <span className="sidebar-label">{item.label}</span>
          </button>
        ))}
      </nav>

      <div className="sidebar-gradient">
        <img src={sidebarGradient || "/placeholder.svg"} alt="decorative" className="gradient-image" />
      </div>
    </aside>
  )
}
