"use client"

import { useState } from "react"
import "./SignupPage.css"

export function SignupPage() {
  const [isLogin, setIsLogin] = useState(false)
  const [email, setEmail] = useState("")
  const [name, setName] = useState("")
  const [password, setPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [loading, setLoading] = useState(false)

  const handleSignup = async (e) => {
    e.preventDefault()
    setError("")
    setSuccess("")

    if (password !== confirmPassword) {
      setError("Passwords do not match")
      return
    }

    setLoading(true)

    try {
      const response = await fetch("http://localhost/myapi/signup.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name,
          email,
          password,
        }),
      })

      const data = await response.json()

      if (!data.success) {
        setError(data.error || "Signup failed")
        return
      }

      setSuccess("Signup successful! Now log in with your credentials.")
      setName("")
      setEmail("")
      setPassword("")
      setConfirmPassword("")
      setIsLogin(true)
    } catch (err) {
      setError("An error occurred. Please try again.")
      console.log("[v0] Signup error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleLogin = async (e) => {
    e.preventDefault()
    setError("")
    setSuccess("")

    setLoading(true)

    try {
      const response = await fetch("http://localhost/myapi/login.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          email,
          password,
        }),
      })

      const data = await response.json()

      if (!data.success) {
        setError(data.error || "Login failed")
        return
      }

      // Store user info in localStorage
      localStorage.setItem("user", JSON.stringify(data.user))
      setSuccess("Login successful! Redirecting...")

      // Redirect to dashboard/home after 1 second
      setTimeout(() => {
        window.location.href = "/"
      }, 1000)
    } catch (err) {
      setError("An error occurred. Please try again.")
      console.log("[v0] Login error:", err)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h1 className="auth-title">{isLogin ? "LOGIN" : "SIGN UP"}</h1>

        <form onSubmit={isLogin ? handleLogin : handleSignup}>
          {!isLogin && (
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                placeholder="John Doe"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required={!isLogin}
              />
            </div>
          )}

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              placeholder="your@email.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {!isLogin && (
            <div className="form-group">
              <label>Confirm Password</label>
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>
          )}

          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}

          <button type="submit" className="auth-button" disabled={loading}>
            {loading ? (isLogin ? "Logging in..." : "Signing up...") : isLogin ? "Login" : "Sign Up"}
          </button>

          <p className="auth-link">
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <a href="#" onClick={(e) => {
              e.preventDefault()
              setIsLogin(!isLogin)
              setError("")
              setSuccess("")
              setEmail("")
              setPassword("")
              setConfirmPassword("")
            }}>
              {isLogin ? "Sign up" : "Log in"}
            </a>
          </p>
        </form>
      </div>
    </div>
  )
}
