"use client"

import { useState } from "react"
import { FormInput } from "./FormInput"
import { FormButton } from "./FormButton"
import { RoleSelect } from "./RoleSelect"
import "./LoginPage.css"

export function LoginPage({ onSignupClick, onForgotClick }) {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    role: "User",
  })
  const [errors, setErrors] = useState({})
  const [loading, setLoading] = useState(false)
  const [successMessage, setSuccessMessage] = useState("")

  const validateForm = () => {
    const newErrors = {}
    if (!formData.username.trim()) {
      newErrors.username = "Username is required"
    }
    if (!formData.password.trim()) {
      newErrors.password = "Password is required"
    }
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }))
    }
  }

  const handleRoleChange = (value) => {
    setFormData((prev) => ({
      ...prev,
      role: value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!validateForm()) return

    setLoading(true)
    setSuccessMessage("")
    try {
      const response = await fetch("/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      })

      if (response.ok) {
        setSuccessMessage("Login successful!")
        setFormData({ username: "", password: "", role: "User" })
      } else {
        setErrors({ submit: "Login failed. Please try again." })
      }
    } catch (error) {
      setErrors({ submit: "Network error. Please try again." })
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="login-container">
      <div className="login-background"></div>

      <div className="login-wrapper">
        <div className="login-card">
          <h1 className="login-title">LOG IN</h1>

          {successMessage && (
            <div className="bg-green-100 text-green-800 p-3 rounded mb-4 text-sm">{successMessage}</div>
          )}
          {errors.submit && <div className="bg-red-100 text-red-800 p-3 rounded mb-4 text-sm">{errors.submit}</div>}

          <form onSubmit={handleSubmit} className="login-form-space">
            <FormInput
              label="Username"
              name="username"
              type="text"
              placeholder="ex. admin@gmail.com"
              value={formData.username}
              onChange={handleChange}
              error={errors.username}
            />

            <FormInput
              label="Password"
              name="password"
              type="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              error={errors.password}
            />

            <RoleSelect value={formData.role} onChange={handleRoleChange} />

            <div className="login-forgot-link">
              <button type="button" onClick={onForgotClick}>
                Forgot Password?
              </button>
            </div>

            <FormButton type="submit" disabled={loading}>
              {loading ? "Signing in..." : "Sign In"}
            </FormButton>

            <div className="login-signup-link">
              <button type="button" onClick={onSignupClick}>
                Sign up
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}
